var Table = new Mongo.Collection("table");
var Counter = new Mongo.Collection("counter");

if (Meteor.isClient) {
  // counter starts at 0
  Session.setDefault('counter', 0);


	Template.board.helpers({
		rows: function() {
			console.log("Calling Rows");
			return Table.find({}).fetch();			
		},
		columns: function() {
			console.log("Cols: " + this.cols);
			return this.cols
		}

	});



  Template.hello.helpers({
    counter: function () {
			var count = Counter.findOne({});
			console.log("Calling Counter");
			return count;
    }
  });

  Template.hello.events({
    'click button': function () {
      // increment the counter when button is clicked
			var countDoc = Counter.findOne({})
			console.log("Button Clicked");	
			Counter.update(countDoc._id, {$set: { 
					count: (countDoc.count+1) 
				}
			});
    },
		'click p': function() {
			console.log(Counter.findOne({}).count);
		}
  });

	Template.reset.events({
		'click button': function() {
			var countDoc = Counter.findOne({});
			console.log("Reset Clicked");
			Counter.update(countDoc._id, {$set: {count: 0}});
		}
	});
}

if (Meteor.isServer) {
  Meteor.startup(function () {
    // code to run on server at startup
  });
}
