var Table = new Mongo.Collection("table");
var Counter = new Mongo.Collection("counter");

if (Meteor.isClient) {
  // counter starts at 0
  Session.setDefault('counter', 0);


  Template.board.helpers({
  	rows: function() {
  		console.log("Calling Rows");
  		return Table.find({}).fetch();			
  	}
  });

  Template.row.helpers({
  	columns: function(parentContext) {
  		return this.cols
  	}
  });

  Template.row.events({
  	'click td': function(event, template) {
  		console.log("Clicked: " + template.data.row + ", " + this.col);
  		var row = Table.findOne(template.data._id);
  		var cols = row.cols


  		cols[this.col-1].playerMark++
  		Table.update(template.data._id, {$set: {cols: cols}});
  	}
  });

  Template.hello.helpers({
  	counter: function () {
  		var count = Counter.findOne({});
  		console.log("Calling Counter");
  		return count;
  	}
  });

 	Template.hello.events({
	  	'click button': function () {
	      // increment the counter when button is clicked
	      var countDoc = Counter.findOne({});
	      console.log("Button Clicked");	
	      Counter.update(countDoc._id, {$set: { count: (countDoc.count+1)}});
	  	},

	  	'click p': function() {
	  		console.log(Counter.findOne({}).count);
	  	}
	});

  	Template.reset.events({
	  	'click button': function() {
	  		var countDoc = Counter.findOne({});
	  		console.log("Reset Clicked");
	  		Table.find({}).forEach(function (doc) {
	  			var cols = doc.cols
	  			console.log(cols);
	  			cols.forEach(function(cell) {
	  				cell.playerMark = 0;
	  				Table.unsert()
	  				Table.update(doc._id, {$set: {cols: cols}});
	  			});
	  		});
	  	}
  	});
}

if (Meteor.isServer) {
	Meteor.startup(function () {
    // code to run on server at startup
});
}
